let menuBtn = document.querySelector('.menu-btn');
let menum = document.querySelector('.menum');
let logo = document.querySelector('#logodiv');
let city = document.querySelector('.city');
let menu = document.querySelector('.menum');
let head = document.querySelector('.header');
let banner = document.querySelector('.banner');
let isLogoVisible = false;
let isCityVisible = true;
menuBtn.addEventListener('click', function() {
  isLogoVisible = !isLogoVisible;
  if (isLogoVisible) {
    banner.style.gridRowStart= '1';
    banner.style.marginTop= '16vh';
    menu.style.position='fixed';
    menu.style.margin='16vh 0 0 0';
    head.style.position='fixed';
    logo.style.display = 'none';
    city.style.display='grid';
  } else {
    head.style.position='relative';
    logo.style.display = 'grid';
    city.style.display='none';
    
  }
  menuBtn.classList.toggle('active');
  menum.classList.toggle('active');
});